#include <iostream>
#include <string>
#include <vector>
#include <unordered_map>

using namespace std;

unordered_map<string, string> umap = {{"1", "A"},{"2","B"},{"3","C"},{"4","D"},{"5","E"},{"6","F"},{"7","G"},{"8","H"},{"9","I"},{"10","J"},{"11","K"},{"12","L"},{"13","M"},{"14","N"},{"15","O"},{"16","P"}, {"17","Q"}, {"18","R"}, {"19","S"}, {"20","T"}, {"21","U"}, {"22","V"}, {"23","W"}, {"24","X"}, {"25","Y"}, {"26","Z"}};

class Solution {
public:
    unsigned long numDecodings(string s) {
        vector<unsigned long> vec;
        vec.push_back(umap.find(s.substr(0,1)) != umap.end()); // o(0)
        vec.push_back(umap.find(s.substr(0,1)) != umap.end()); // o(1)
        
        for (int i = 2; i <= s.size(); i++) {
            //cout << s.substr(i-2,2) << endl;
            vec.push_back(vec[i-1] + vec[i-2] * (umap.find(s.substr(i-2,2)) != umap.end()));
            cout << i << " " << vec[i] << endl;
        }
        
        return vec[s.size()];
    }
};

int main(int argc, const char * argv[]) {
    string s = "";
    class Solution A;
    cout << A.numDecodings(s) << endl;
    
    return 0;
}
